import React, {Component} from 'react';
// import { import } from '@babel/types';
import logo from '../images/logo.png'; 
//  import logo main header
import '../index.css';

import logo1 from '../images/0.png'; 


class Header extends Component {
  constructor(props){
    super(props);
    this.state = {
      showForm:false,
      info:false,
    }
  }

   info =()=>{ 

    const [NameX , setNameX] = React.useState("")
    const [NameO , setNameO] = React.useState("")

    const handleSubmit =(event)=> {
        console.log(`
      NameX:${NameX}
      NameO:${NameO}
    `);
    event.preventDefault();
    }

    return (
      <div>
      <form onSubmit = {handleSubmit}>
        <div className="form-row">
        <div className="col-6">
        <label>Name  <img className="img1" src={logo} alt="Logo"/></label>
        <input name="nameX" type="text" value={NameX} onChange={e=> setNameX(e.target.value)} ></input>
        </div>
       <div className="col-6">
       <label>Name <img className="img1" src={logo1} alt="Logo"/></label>
        <input name="nameO" type="text" value={NameO} onChange={e=> setNameO(e.target.value)} ></input>
       </div> 
     </div>
     <div>
       <label>
         <button type="submit" value="Submit"
       >Start Game</button></label>
       </div>
      </form>
    </div>
    )
  }

   state = {showForm:false

  } 
   showForm =()=> {
     return (
       <div>
         <form onSubmit = {this.handleSubmit}>
           <div className="form-row">
           <div className="col-6">
           <label>Name  <img className="img1" src={logox} alt="Logo"/></label>
           <input type="text"></input>
           </div>
          <div className="col-6">
          <label>Name <img className="img1" src={logoO} alt="Logo"/></label>
           <input type="text"></input>
          </div> 


        </div>
        <div>
          <label className=""><button type="submit" value="Submit">Start Game</button></label>
          </div>
         </form>
       </div>
     )
   }


  render(){
  return( 
    // Fragment eleminate the need to  wrapp every component into div
    <React.Fragment> 
        <div className="main-header">
        {/* render logo main header */}
        <img src={logo} alt="Logo" />
        {/* main header */}
            <div>
                 <h1>TICTOCTOE</h1>
            </div>
        {/* button  start */}
            <button onClick={ () => this.setState({info:true})}>startGame</button>
            {this.state.info ? this.info():null }
            <button onClick ={ () => this.setState({showForm: true})}>Start</button>
            {this.state.showForm ? this.showForm(): null }     
        </div>
        {/* <Info/> */}

     </React.Fragment>
  );
  }
}
export default Header;
