// react class component 
import React from 'react';
import Cell from './Cell';

class Board extends React.Component {  
        handleClick(i){
                //  create a copy of the squares to be modified instead of modifying the original
                // using slice() method and the take the turns between two players 
                const cells = this.state.cells.slice();
                // Change the board's handleClick functiion to return early by ignoring a click if some has won the game 
                // or if the a square is already filled: 
                if (determineTheWinner(cells) || cells[i]){
                        return;
                }
                       cells[i] = this.state.xisNext ? "X": "O";
                //        Save new outcome of cells clicked  to the existing cells data
                       this.setState ({    
                           cells:cells,
                           xisNext: !this.state.xisNext,
                });
        }

        // Square should be rendered and attach an event listiners to it.
        renderSquare(i){
                return (<Cell 
                       value ={this.state.cells[i]} 
                        // a fuction that square can call when clicked  
                        onClick = {()=> this.state.onClick(i)}
                        />
                );
        }
        render(){
        //   Check if the player has won or lost
          const winner = determineTheWinner(this.state.cells)
                let  statusHis;
                if (winner){
                        statusHis = 'winner:' + winner;
                }else{
                       statusHis = 'Next Player: ' + (this.state.xisNext ? "X":"O")   
                }

      return ( 
      <div className="container">
       <div className="status">{statusHis}</div>
        <div className="row">
                {this.renderSquare(0)}
                {this.renderSquare(1)}           
                {this.renderSquare(2)}
        </div>
        <div className="row">
                {this.renderSquare(3)}
                {this.renderSquare(4)}           
                {this.renderSquare(5)}
        </div>
        <div className="row">
                {this.renderSquare(6)}
                {this.renderSquare(7)}           
                {this.renderSquare(8)}
        </div>
      </div>
      )}
    }
    export default Board;

function determineTheWinner(cells){
        const lines=[
                [0,1,2],
                [3,4,5],
                [6,7,8],
                [0,3,6],
                [1,4,7],
                [2,5,8],
                [0,4,8],
                [2,4,6],

        ];
        for (let i=0; i <  lines.length  ; i ++){
                const [a,b,c] = lines[i];
                if (cells[a] && cells[a] === cells[b] && cells[a]=== cells[c]) 
                    {
                            return cells[a];
                    }
        }
        return null;
}
