import React, {Component} from 'react';
// import { import } from '@babel/types';
import logo from '../images/logo.png'; 
//  import logo main header
import '../index.css';
import logox from '../images/1.png'; 
import logoO from '../images/0.png'; 

import logo1 from '../images/0.png'; 


class Header extends Component {
  constructor(props){
    super(props);
    this.state = {
      // showForm:false,
      NameX: "",
      NameO:"",
    }
  }
  handleSubmit(event){
    event.preventDefault();
    const data = this.state;
    console.log(data);
  }
  inputChange =(event)=> {
    event.preventDefault();
    this.setState({
      [event.target.name]:event.target.value,
      [event.target.name]:event.target.value

    })
  }
  render(){
    const {NameX} = this.state
    const {NameO} = this.state

     return (
       <div>
         <form onSubmit = {this.handleSubmit}>
           <div className="form-row">
           <div className="col-6">
           <label>Name  <img className="img1" src={logox} alt="Logo"/></label>
           <input name="NameX" value ={NameX}  type="text" onChange={this.inputChange}></input>
           </div>
          <div className="col-6">
          <label>Name <img className="img1" src={logoO} alt="Logo"/></label>
           <input name="NameO" value={NameO} type="text" onChange={this.inputChange}></input>
          </div> 
        </div>
        <div>
          <label className=""><button type="submit" value="Submit">Start Game</button></label>
          </div>
         </form>
       </div>
     )
   }
  render(){
  return( 
    // Fragment eleminate the need to  wrapp every component into div
    <React.Fragment> 
        <div className="main-header">
        {/* render logo main header */}
        <img src={logo} alt="Logo" />
        {/* main header */}
            <div>
                 <h1>TICTOCTOE</h1>
            </div>
        {/* button  start */}

            // <button onClick ={ () => this.setState({showForm: true})}>Start</button>
            // {this.state.showForm ? this.showForm(): null }     
        </div>
        {/* <Info/> */}

     </React.Fragment>
  );
  }
}
export default Header;
