// react class component 
import React from 'react';
import Cell from './Cell';
export default  class Board extends React.Component {
        constructor (props){
                super(props)
                this.state ={ 
                        squares :Array(9).fill(null),
                        xIsNext:true,
                };
        } 

handleClick(i){
        const squares = this.state.squares.slice();
        squares[i] = this.state.xIsNext ? "X":"O"
        this.setState = ({
                squares:squares,
                xIsNext: !this.state.xIsNext
})
}
renderSquare(i){ 
        return (
        <Cell value ={this.state.squares[i]} 
              onClick = {()=>this.handleClick(i)}
                />
        );
}
render(){

      const status= 'next player: X'
      return( 
      <div className="container">
      <div className="status">{status}</div>
        <div className="row">
                {this.renderSquare(0)}
                {this.renderSquare(1)}           
                {this.renderSquare(2)}
        </div>
        <div className="row">
                {this.renderSquare(3)}
                {this.renderSquare(4)}           
                {this.renderSquare(5)}
        </div>
        <div className="row">
                {this.renderSquare(6)}
                {this.renderSquare(7)}           
                {this.renderSquare(8)}
        </div>
      </div>
      )}
    }


