// Import needed dependncies 
import React, {Component} from 'react';
import '../index.css';
import logo from '../images/1.png'; 
import logo1 from '../images/0.png'; 



export default class Application extends Component {
     constructor(props){
       super(props);
       this.state ={
         NameX: "",
         NameO:""
       }
       this.updateInput = this.updateInput.bind(this)
       this.handleSubmit = this.handleSubmit.bind(this)
     }
     updateInput(event) {
       this.setState({
         NameX:event.target.value,
         NameO:event.target.value
       })
     }

     handleSubmit(){
       console.log("you in put is :" + this.state.NameX);
       console.log("you other input is:" + this.state.NameO);
     }
     render() {
       return (
                <div>
                    <form>
                      <div className="form-row">
                      <div className="col-6">
                      <label>Name  <img className="img1" src={logo} alt="Logo"/></label>
                      <input name="nameX" type="text" onChange={this.updateInput} ></input>
                      <input name="nameX" type="submit" onChange={this.handleSubmit} ></input>

                      </div>
                    <div className="col-6">
                    <label>Name <img className="img1" src={logo1} alt="Logo"/></label>
                      <input name="nameO"  type="text" onChange ={this.updateInput} ></input>
                      <input name="nameX"  type="submit" onChange={this.handleSubmit} ></input>
                    </div> 
                  </div>
                  <div>
                    <label>
                      <button type="submit" value="Submit"
                    >Start Game</button></label>
                    </div>
                    </form>
                  </div>

       )
     }
}


