import React, {Component} from 'react';

import logo from '../images/logo.png'; 
//  import logo main header
import '../index.css';
import logox from '../images/1.png'; 
import logoO from '../images/0.png'; 
import Game from './Game';
// import Board from './Board';
export default class Header extends Component {
  constructor(){
    super();
    this.state ={
         NameX:" ", 
         NameO:" "
    }
  this.PlayersNameChange = PlayersName.bind(this);
  this.handleSubmit = this.handleSubmit.bind(this);
PlayersName =({target})=>{
    this.setState({[target.name]:target.value})

  }
  // Use a react hook to get aour updated data to the DOM 
  handleSubmit =(event)=>{ 
    event.preventDefault();
    const data = this.state
    console.log("Final Data is ",data)
  };
   showForm = ()=> {
     return (
       <div>
         <form onSubmit = {this.handleSubmit}>
           <div className="form-row">
           <div className="col">
           <label>Name  <img className="img1" src={logox} alt="Logo"/></label>
           <input type="text"  name="NameX" value={this.state.NameX} onChange={PlayersName}></input>
           </div>

          <div className="col">
          <label>Name <img className="img1" src={logoO} alt="Logo"/></label>
           <input type="text" name="NameO"  value={this.state.NameO} onChange={PlayersName}></input>
           </div>
           <div>
             <hr/>
          <label> 
             <button className="btn btn-success">Start Game</button>
          </label>
          </div>
          </div> 
         </form>
       </div>
     )
    }


  // startGameFunction =()=>{ 
  //   return (
  //           <div> 
  //               <button>start</button>
  //           </div>
  //   )
  }
  render(){
  return( 
    // Fragment eleminate the need to  wrapp every component into div
    <React.Fragment> 
        <div className="main-header">
        {/* render logo main header */}
        <img src={logo} alt="Logo" />
        {/* main header */}
            <div>
                 <h1>TICTOCTOE</h1>
            </div>
        <div className="mx-auto">
        <button className="btn btn-secondary" onClick ={() => this.setState({showForm: true})}>Start</button>
            {this.state.showForm ? this.showForm(): null }     
         </div>
        </div> 
        {/* <Game/> */}
     </React.Fragment>
  );
  }
}
