import React, {Component, useState} from 'react';
// import { import } from '@babel/types';
import logo from '../images/logo.png'; 
//  import logo main header
import '../index.css';
import logox from '../images/1.png'; 
import logoO from '../images/0.png'; 
import Board from './Board';






class Header extends Component {
  constructor (props){
    super(props);
    this.state = {
      NameX:'',
      NameO:''
    }
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  // end of Constructor

  handleChange(event){
    this.setState (
      {NameX:event.target.value,
       NameO:event.target.value
      }

      )

  }
  handleSubmit(event){
    alert ("you clicked button:" + this.state.value);
    event.preventDefault();

  }

  render(){
    return (
      <div>
           <form onSubmit = {this.handleSubmit}>
             <div className="form-row">
             <div className="col-6">
             <label>Name  <img className="img1" src={logox} alt="Logo"/></label>
             <input type="text" value={this.state.value} onChange={this.handleChange}></input>
             </div>
            <div className="col-6">
            <label>Name <img className="img1" src={logoO} alt="Logo"/></label>
             <input type="text"></input>
             <div>
            <label className="start">
            {/* <button type="submit" value="Start Gane"></button> */}
            <input type="submit" value="Start Game "/>
            </label>
            </div>
            </div> 
          </div>
           </form>

        {/* //  Header to the console  */}
        <React.Fragment> 
        <div className="main-header">
        {/* render logo main header */}
        <img src={logo} alt="Logo" />
        {/* main header */}
            <div>
                 <h1>TICTOCTOE</h1>
            </div>
        {/* button  start */}
            <button >Start</button>
            {/* {this.state.showForm ? this.showForm(): null }      */}
        </div>
        {/* <Info/> */}
     </React.Fragment>
         </div>

    )

  }

}
export default Header;


