import React, {Component} from 'react';
// import { import } from '@babel/types';
import logo from '../images/logo.png'; 
//  import logo main header
import '../index.css';
import logox from '../images/1.png'; 
import logoO from '../images/0.png'; 


class Header extends Component {
  constructor(props){
    super(props);
    this.state = { showForm:false,


  }
  // this.showForm = this.showForm.bind(this)        
  } 

   showForm =()=> {
    //  const
     return (
       <div>
         <form onSubmit = {this.handleSubmit}>
           <div className="form-row">
           <div className="col-6">
           <label>Name  <img className="img1" src={logox} alt="Logo"/></label>
           <input type="text"></input>
           </div>
          <div className="col-6">
          <label>Name <img className="img1" src={logoO} alt="Logo"/></label>
           <input type="text"></input>
          </div> 


        </div>
        <div>
          <label className=""><button type="submit" value="Submit" onClick= {()=> this.displayPlayerInfo("Demo1")}
          >Start Game</button></label>
          </div>
         </form>
       </div>
     )
   }
  render(){
  return( 
    // Fragment eleminate the need to  wrapp every component into div
    <React.Fragment> 
        <div className="main-header">
        {/* render logo main header */}
        <img src={logo} alt="Logo" />
        {/* main header */}
            <div>
                 <h1>TICTOCTOE</h1>
            </div>
        {/* button  start */}
            <button onClick ={ () => this.setState({showForm: true})}>Start</button>
            {this.state.showForm ? this.showForm(): null }     
        </div>
        {/* <Info/> */}

     </React.Fragment>
  );
  }
}
export default Header;
