import Header from './Header';
// import Info from './Info';
// import Game from './Game';


 class App extends Component {
   state ={
     fields:{}
   }
   onChange =  updatedValue =>{
     this.setState({ 
       fields:{
        //  keep the original values
            ...this.state.fields, 
            // grab new updated values and  put in our field
            ...updatedValue}

      })
    //  console.log("App got :", fields )


   }
       render() {
          return (
            <React.Fragment>
            <div className="info">
                 <Header onChange={fields => this.onChange(fields)}/> 
                 <p>{JSON.stringify(this.state.fields)}</p>
             </div>
            </React.Fragment>
            );
          } 
        }

 export default App;
