// Import needed dependncies 
import React from 'react';
import '../index.css';



export default class Info extends React.Component{
  constructor(props) {
    super(props)
    this.state = { 
           Player_1: null,
           Player_2:null
    }
  }
   handleSubmit =(event)=>{
     event.PreventDefault() 
   }
   handleInputChange= (event) =>{
     this.setState({
      [event.target.name]:event.target.value,
      [event.target.name]:event.target.value
     })
   }
  render(){
    const {Player_1} = this.state
    const {Player_2} = this.state

    // JSX
    return(
      <div> 
        <h1> Enter Name</h1>
        <p> Player_1:{Player_1}</p>
        <p> Player_2:{Player_2}</p>

        <form onSubmit = {this.handleSubmit}>
          <p><input className="fName" type="text" placeholder ="yourname" name="Player_1" onChange={this.handleInputChange}/></p>
          <p><input className="lName" type="text" placeholder ="Player_2" name="Player_2" onChange={this.handleInputChange} /></p>
          <p><button>Start Game</button></p>
        </form>
      </div>
    )
  }
}
