import React, {Component}  from 'react';
import Board from './Board'; 


export default class  Game extends Component {
    // Set intial constructor 
    constructor(props){
        super(props) 
        this.state ={

            xisTheNext:true, // set x as a flag the determine if it X or O time to go, therefore the first player is X
            gameNum:0, // default value of some number is 0, since  there is not steps at thebegining
            prevStatus:[ // set prevStatus  of players
                {squares:Array(9).fill(null)} // set each item to null because none is playing at the begining
            ]  //saves the prevStatus of the steps in the game
        }
    }
    // Create a handle click function that will change the status of each cell every
    // time the user clicks on  it.
handleClick(i){
    const prevStatus =this.state.prevStatus.slice(0,this.state.gameNum +1);
    const currentStatus =prevStatus[prevStatus.length-1];
    const squares = currentStatus.squares.slice();
          squares[i] = this.state.xisTheNext ? "X":"O";
          this.setState ({
              prevStatus:prevStatus.concat({
                  squares:squares
              }),
              xisTheNext:!this.state.xisTheNext,
              gameNum : prevStatus.lenght
          })
}
render(){
    const prevStatus =this.state.prevStatus;
    const currentStatus = prevStatus[this.state.gameNum];

  return(
    <div className="game">
    <div className="game-board">
    {/* get two properties from Board  which cells arrays and onclick Method */}
          <Board onClick ={(i)=> this.handleClick(i)}
            squares ={currentStatus.squares}
          />
     </div> 
    </div> 
  );
  }
}
