import React, {Component}  from 'react';
import { array } from 'prop-types';
import Board from './Board'; 


class  Game extends Component {
    constructor(props){
        // first function that will be called in each react that will be created in the R component
        super(props)
        this.state ={

            xisTheNext:true, // set x as a flag the determine if it X or O time to go
            stepNum: 0, // default value of some number is 0, since  there is not steps at thebegining
            history:[
                {cells:array(9).fill(null)} // set each item to null because none is playing at the begining


            ]  //saves the history of the steps in the game
        }
    }


render(){
    const history =this.state.history;
    const current = history[this.state.stepNum];
  return(
    <div className="game">
    <div className="game-board">
          <Board onClick={(i)=> this.onClick(i)}
            cells ={current.cells}
          />
    </div>
    </div>

  );
  }

}
export default Cell;
