// react class component 
import React, {Component}  from 'react';
import Cell from './Cell'; 

export default class Board extends Component { 
  renderSquare(i){
    return <Cell value={this.props.squares[i]}
            onClick ={() =>this.props.onClick[i]}
            />
  }
  render(){
      return(
      <div className="container">
        <div className="row">
                {this.renderSquare(0)}
                {this.renderSquare(1)}           
                {this.renderSquare(2)}
        </div>
        <div className="row">
                {this.renderSquare(0)}
                {this.renderSquare(1)}           
                {this.renderSquare(2)}
        </div>
        <div className="row">
                {this.renderSquare(0)}
                {this.renderSquare(1)}           
                {this.renderSquare(2)}
        </div>
      </div>
      )
     }

