// react class component 
import React from 'react';
import Cell from './Cell';


class Board extends React.Component { 
        // adding a constructor to  the board's initial state to contain anarray
        //  of 9 nulls corresponding to the squares
        constructor(props){
                super(props) 
                this.state = { 
                        squares:Array(9).fill(null),
                        xisNext:true,

                };

        }
        // 
        handleClick(i){
                //  create a copy of the squares to be modified instead of modifying the original
                // using slice() metho
                const newSquares = this.state.squares.slice();
                       newSquares[i] = this.state.xisNext ? "X":"O" ;
                //        Save new outcome of squares clicked  to the existing cellls data
                       this.setState ({ squares:newSquares})
        }
        // Square should be rendered and attach an event listiners to it.
        renderSquare(i){
                return <Cell 
                        value ={this.state.squares[i]} 
        // a fuction that square can call when clicked  
                        onClick = {()=>this.handleClick(i)}
                        />
        }
  render(){
      return ( 
      <div className="container">
        <div className="row">
                {this.renderSquare(0)}
                {this.renderSquare(1)}           
                {this.renderSquare(2)}
        </div>
        <div className="row">
                {this.renderSquare(3)}
                {this.renderSquare(4)}           
                {this.renderSquare(5)}
        </div>
        <div className="row">
                {this.renderSquare(6)}
                {this.renderSquare(7)}           
                {this.renderSquare(8)}
        </div>
      </div>
      )}
    }
    export default Board;
