import React, {Component}  from 'react';
import Cell from './Cell';


export default class Board extends Component { 
        renderSquare(i){
                return <Cell value={this.props.cells[i]}  // return an array cells as properties
                        onClick ={() =>{this.props.onClick[i]}}


                />
        }
//   renderSquare(i){
