// Import needed dependncies 
import React from 'react';
import '../index.css';



export default class Info extends React.Component{
  constructor(props) {
    super(props)
    this.state = { 
           Player_1: null,
           Player_2:null
    }
  }
   handleSubmit =(event)=>{
     event.PreventDefault() 
   }
   handleInputChange= (event) =>{
     this.setState({
      [event.target.name]:event.target.value,
      [event.target.name]:event.target.value
     })
   }
  render(){
    const {Player_1} = this.state
    const {Player_2} = this.state

    // JSX
    return(
      <div> 
        <h1> EnterName</h1>
        <form onSubmit = {this.handleSubmit}>
              <div className ="form-row">
                <div className="col-6">
                          <p > Player_1:<strong>{Player_1}</strong></p>
                          <p><input className="form-control" type="text" placeholder ="yourname" name="Player_1" onChange={this.handleInputChange}/></p>
                </div>
                <hr></hr>
                  <div className="col-6">
                           <p> Player_2:<strong>{Player_2}</strong></p>
                           <p><input className="form-control" type="text" placeholder ="Player_2" name="Player_2" onChange={this.handleInputChange} /></p>
                  <div>
                   </div>
                 <p className="row-ms"><button>Start Game</button></p>
              </div>
           </div>
        </form>

      </div>
    )
  }
}
