// react class component 
import React from 'react';
import Cell from './Cell';


class Board extends React.Component { 
        // adding a constructor to  the board's initial state to contain anarray
        //  of 9 nulls corresponding to the squares
        constructor(props){
                super(props) 
                this.state = { 
                        cells:Array(9).fill(null),
                };
        }
        renderSquare(i){
                return <Cell value ={this.props.cells[i]}/>
        }


  render(){
      return ( 
      <div className="container">
        <div className="row">
                {this.renderSquare(0)}
                {this.renderSquare(1)}           
                {this.renderSquare(2)}
        </div>
        <div className="row">
                {this.renderSquare(3)}
                {this.renderSquare(4)}           
                {this.renderSquare(5)}
        </div>
        <div className="row">
                {this.renderSquare(6)}
                {this.renderSquare(7)}           
                {this.renderSquare(8)}
        </div>
      </div>
      )}
    }
    export default Board;
