// react class component 
import React from 'react';
import Cell from './Cell';
class Board extends React.Component { 
handleClick(i){
                const squares  = this.state.squares.slice();
                if (determineTheWinner(squares) || squares[i]){
                        return;
                }
                squares[i] = this.state.xIsNext ? "x":"O";
                this.setState =({
                        squares:squares,
                        xIsNext: ! this.state.xIsNext ? "X":"O",
                });

        }

renderSquare(i){

        return 

        (<Cell 
                value ={this.props.squares[i]} 
                onClick = {()=>this.props.handleClick(i)}
                />
        );
}

render(){
      const winner = determineTheWinner(this.state.square) 
      let status;
          if (winner){
                  status= "the winner is:" + winnner;
          }else {
                  status = "next player:" + (this.state.xIsNext ? "x":"O")
          }

      return( 
      <div className="container">
       <div className="stattus">{status}</div> 
        <div className="row">
                {this.renderSquare(0)}
                {this.renderSquare(1)}           
                {this.renderSquare(2)}
        </div>
        <div className="row">
                {this.renderSquare(3)}
                {this.renderSquare(4)}           
                {this.renderSquare(5)}
        </div>
        <div className="row">
                {this.renderSquare(6)}
                {this.renderSquare(7)}           
                {this.renderSquare(8)}
        </div>
      </div>
      )}
    }
    export default Board;

function determineTheWinner(squares){
        const lines=[
                [0,1,2],
                [3,4,5],
                [6,7,8],
                [0,3,6],
                [1,4,7],
                [2,5,8],
                [0,4,8],
                [2,4,6],

        ];
        for (let i=0; i <  lines.length  ; i ++){
                const [a,b,c] = lines[i];
                if (squares[a] && squares[a] ===
                    squares[b] && squares[a]=== squares[c]) {
                            return squares[a];
                    }
        }
        return null;
}
