import React, {Component}  from 'react';
import Board from './Board'; 
import '../index.css';
export default class  Game extends Component { 
// adding a constructor to  the Game  initial state to contain an array
    render(){ 
            return( 
              <React.Fragment>            
                  <div className="game">
                  <div className="game-board">
                      {/* <Info/> */}
                      <Board 
                      // squares={current.squares}
                      // OnClick = {(i) => this.handleClick(i)}  
                      />
                 </div>
                   <div>
                   <p className="NameX"> NameX: Musafiri</p>
                   <p className="NameO"> NameO: Kayambi</p>
                    </div>
                  <div className="game-info">  
                  </div>
                  </div>

      {/* New Game and reset the Game */}
                  <div className="row mx-md-n5">
                  <div className="col px-md-5">
                      <div className="bg-light">
                      <button className="NewGame">New Game</button></div>
                  </div>
                  <div className="col px-md-5">
                      <div className="bg-light">
                      <button className="ResetGame">Reset Ga</button></div>
                  </div>
                    </div>
              </React.Fragment>  
                  );
  }
}
