// Import needed dependncies 
import React, {Component} from 'react';
import '../index.css';
import logo from '../images/1.png'; 
import logo1 from '../images/0.png'; 

export default function Info () {
  const [NameX , setNameX] = React.useState("")
  const [NameO , setNameO] =React.useState("")

  const handleSubmit =(event)=> {
      return (`
    NameX:${NameX}
    NameO:${NameO}
  `);
  event.preventDefault();
  }
  return (
    <div>
    <form onSubmit = {handleSubmit}>
      <div className="form-row">
      <div className="col-6">
      <label>Name  <img className="img1" src={logo} alt="Logo"/></label>
      <input name="nameX" type="text" value={NameX} onChange={e=> setNameX(e.target.value)} ></input>
      </div>
     <div className="col-6">
     <label>Name <img className="img1" src={logo1} alt="Logo"/></label>
      <input name="nameO" type="text" value={NameO} onChange={e=> setNameO(e.target.value)} ></input>
     </div> 
   </div>
   <div>
     <label>
       <button type="submit" value="Submit"
     >Start Game</button></label>
     </div>
    </form>
  </div>
  )
}



















